mapper.select:
SELECT count(*)
FROM comments
START WITH cmt_id = #{commentId}
CONNECT BY PRIOR cmt_id = cmt_ref
ORDER SIBLINGS BY created_date DESC;

mapper.update:
UPDATE comments
SET CMT_CONTENT = #{commentContent}
WHERE CMT_ID = #{commentId};

UPDATE comments
SET DELETED = 'Y'
WHERE CMT_ID = #{commentId};
