mapper.update:
UPDATE board
SET board_viewcount = board_viewcount + 1
    WHERE board_id = #{boardId}

UPDATE board
SET board_title=#{boardTitle}, board_content=#{boardContent}
    WHERE board_id = #{boardId}

UPDATE board
SET deleted='Y'
    WHERE board_id = #{boardId}

mapper.select:
SELECT count(*)
    FROM boardlike
    WHERE board_id = #{boardId} AND user_id = #{userId}

SELECT count(*)
    FROM bookmark
    WHERE board_id = #{boardId} AND user_id = #{userId}

SELECT count(*)
    FROM hashtag
    WHERE tag_name = #{tagName}

SELECT tag_id
    FROM hashtag
    WHERE tag_name = #{tagName}
