mapper.select:
SELECT count(tag_id)
	FROM hashtag
	WHERE tag_name = #{tag}

mapper.update:
UPDATE board
SET board_viewcount = board_viewcount + 1
	WHERE board_id = #{boardId}

UPDATE board
SET board_title = #{boardTitle},
    board_content = #{boardContent},
    board_thumbnail = #{boardThumbnail}
	WHERE board_id = #{boardId}

UPDATE board
SET deleted= 'Y'
	WHERE board_id = #{boardId}

UPDATE sportboard
SET map_img = #{mapImg}
	WHERE board_id = #{boardId}

mapper.select:
SELECT count(*)
	FROM boardlike
	WHERE board_id = #{boardId}

mapper.delete:
DELETE
	FROM boardlike
	WHERE like_id = #{likeId} AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM BOOKMARK
WHERE board_id = #{boardId} AND user_id = #{userId}

mapper.delete:
DELETE
	FROM bookmark
	WHERE board_id = #{boardId} AND user_id = #{userId}
