mapper.select:
SELECT COUNT(*)
FROM follow
WHERE follower_id = #{userId}

SELECT COUNT(*)
FROM follow
WHERE followee_id = #{userId}

SELECT COUNT(*)
FROM follow
WHERE follower_id = #{userId} AND followee_id = #{followeeId}

mapper.delete:
    DELETE FROM follow
	WHERE follow_id = (
		SELECT MAX(follow_id) FROM follow
		WHERE followee_id = #{followeeId}
		AND follower_id = #{userId}
	)
