mapper.select:
WITH f2 AS (SELECT f.*, dr.record_intake, dr.diet_id, dr.meal_time, r.*, (record_intake/food_intake) as serving FROM food f JOIN dietrecord dr ON f.food_id = dr.food_id JOIN record r ON dr.record_id = r.record_id WHERE r.user_id = #{userId} AND r.deleted = 'N'
AND r.do_date like to_date('2024-03-06', 'YYYY-MM-DD')
AND upper(dr.meal_time) = #{mealTime}
) SELECT f2.food_id, f2.food_name, f2.food_group, (food_cal * coalesce(serving, 1)) as food_cal, (food_protein * coalesce(serving, 1)) as food_protein, (food_provi * coalesce(serving, 1)) as food_provi, (food_carbo * coalesce(serving, 1)) as food_carbo, (food_chole * coalesce(serving, 1)) as food_chole, (food_salt * coalesce(serving, 1)) as food_salt, record_intake, diet_id, record_id, user_id, do_date, serving, f2.meal_time FROM f2 ORDER BY f2.diet_id
