mapper.select:
with f2 as (SELECT f.*, rank() OVER (ORDER BY f.food_id) as rank FROM food f LEFT JOIN customfood cf ON f.food_id = cf.food_id WHERE cf.food_id IS NULL
AND f.food_name LIKE '%' || #{searchWord} || '%'
) SELECT * FROM f2 WHERE rank BETWEEN ((#{nowPage}-1)*#{receivePage}+1) and (#{nowPage}*#{receivePage}) order by f2.rank

WITH f2 as (SELECT f.*, cf.user_id, cf.created_date, rank() OVER (ORDER BY f.food_id) as rank FROM food f JOIN customfood cf ON f.food_id = cf.food_id WHERE cf.user_id = #{userId}
AND f.food_name LIKE '%' || #{searchWord} || '%'
) SELECT * from f2 WHERE rank BETWEEN ((#{nowPage}-1)*#{receivePage}+1) and (#{nowPage}*#{receivePage}) ORDER BY f2.rank
